/**
 * Template Manager
 * 
 * Handles loading and rendering of project document templates.
 * Supports different template formats for architecture, requirements, and design documents.
 */

import { readFile, readdir, stat } from 'fs/promises';
import { join, dirname } from 'path';
import { fileURLToPath } from 'url';
import { createLogger } from './logger.js';

const logger = createLogger('TemplateManager');

export type ArchitectureTemplate = 'arc42' | 'freestyle';
export type RequirementsTemplate = 'ears' | 'freestyle';
export type DesignTemplate = 'comprehensive' | 'freestyle';

export interface TemplateOptions {
  architecture?: ArchitectureTemplate;
  requirements?: RequirementsTemplate;
  design?: DesignTemplate;
}

export interface TemplateResult {
  content: string;
  additionalFiles?: Array<{
    relativePath: string;
    content: Buffer;
  }>;
}

export class TemplateManager {
  private templatesPath: string;

  constructor() {
    // Get the directory of this file and navigate to templates
    const currentDir = dirname(fileURLToPath(import.meta.url));
    this.templatesPath = join(currentDir, 'templates');
  }

  /**
   * Get the default template options
   */
  getDefaults(): Required<TemplateOptions> {
    return {
      architecture: 'arc42',
      requirements: 'ears',
      design: 'comprehensive'
    };
  }

  /**
   * Load and render a template
   */
  async loadTemplate(type: 'architecture' | 'requirements' | 'design', template: string): Promise<TemplateResult> {
    const templatePath = join(this.templatesPath, type, template);

    try {
      // Check if it's a directory (like arc42) or a file
      const stats = await stat(templatePath);

      if (stats.isDirectory()) {
        // Handle directory-based templates (like arc42)
        return await this.loadDirectoryTemplate(templatePath);
      } else {
        // Handle single file templates
        const content = await readFile(`${templatePath}.md`, 'utf-8');
        return { content };
      }
    } catch (error) {
      // Try with .md extension if direct path failed
      try {
        const content = await readFile(`${templatePath}.md`, 'utf-8');
        return { content };
      } catch (fallbackError) {
        logger.error(`Failed to load template: ${type}/${template}`, { error: fallbackError });
        throw new Error(`Template not found: ${type}/${template}`);
      }
    }
  }

  /**
   * Load a directory-based template (like arc42 with images)
   */
  private async loadDirectoryTemplate(templatePath: string): Promise<TemplateResult> {
    const additionalFiles: Array<{ relativePath: string; content: Buffer }> = [];

    // Find the main markdown file
    const files = await readdir(templatePath);
    const markdownFile = files.find(file => file.endsWith('.md'));

    if (!markdownFile) {
      throw new Error(`No markdown file found in template directory: ${templatePath}`);
    }

    const content = await readFile(join(templatePath, markdownFile), 'utf-8');

    // Load additional files (like images)
    await this.loadAdditionalFiles(templatePath, '', additionalFiles);

    return {
      content,
      additionalFiles: additionalFiles.filter(file => !file.relativePath.endsWith('.md'))
    };
  }

  /**
   * Recursively load additional files from template directory
   */
  private async loadAdditionalFiles(
    basePath: string, 
    relativePath: string, 
    additionalFiles: Array<{ relativePath: string; content: Buffer }>
  ): Promise<void> {
    const currentPath = join(basePath, relativePath);
    const items = await readdir(currentPath);

    for (const item of items) {
      const itemPath = join(currentPath, item);
      const itemRelativePath = relativePath ? join(relativePath, item) : item;
      const stats = await stat(itemPath);

      if (stats.isDirectory()) {
        // Recursively process subdirectories
        await this.loadAdditionalFiles(basePath, itemRelativePath, additionalFiles);
      } else if (!item.endsWith('.md')) {
        // Load non-markdown files as additional files
        const content = await readFile(itemPath);
        additionalFiles.push({
          relativePath: itemRelativePath,
          content
        });
      }
    }
  }

  /**
   * Validate template options
   */
  validateOptions(options: TemplateOptions): void {
    const validArchitecture: ArchitectureTemplate[] = ['arc42', 'freestyle'];
    const validRequirements: RequirementsTemplate[] = ['ears', 'freestyle'];
    const validDesign: DesignTemplate[] = ['comprehensive', 'freestyle'];

    if (options.architecture && !validArchitecture.includes(options.architecture)) {
      throw new Error(`Invalid architecture template: ${options.architecture}. Valid options: ${validArchitecture.join(', ')}`);
    }

    if (options.requirements && !validRequirements.includes(options.requirements)) {
      throw new Error(`Invalid requirements template: ${options.requirements}. Valid options: ${validRequirements.join(', ')}`);
    }

    if (options.design && !validDesign.includes(options.design)) {
      throw new Error(`Invalid design template: ${options.design}. Valid options: ${validDesign.join(', ')}`);
    }
  }

  /**
   * Get available templates for each type
   */
  async getAvailableTemplates(): Promise<{
    architecture: ArchitectureTemplate[];
    requirements: RequirementsTemplate[];
    design: DesignTemplate[];
  }> {
    return {
      architecture: ['arc42', 'freestyle'],
      requirements: ['ears', 'freestyle'],
      design: ['comprehensive', 'freestyle']
    };
  }
}

