  const templatePath = join(this.templatesPath, type, template);

  try {
    // Check if it's a directory (like arc42) or a file
    const stats = await stat(templatePath);

    if (stats.isDirectory()) {
      // Handle directory-based templates (like arc42)
      return await this.loadDirectoryTemplate(templatePath);
    } else {
      // Handle single file templates
      const content = await readFile(`${templatePath}.md`, 'utf-8');
      return { content };
    }
  } catch (error) {
    // Try with .md extension if direct path failed
    try {
      const content = await readFile(`${templatePath}.md`, 'utf-8');
      return { content };
    } catch (fallbackError) {
      logger.error(`Failed to load template: ${type}/${template}`, fallbackError as Error);
      throw new Error(`Template not found: ${type}/${template}`);
    }
  }
}

