/**
 * System Prompt Resource Tests
 * 
 * Tests for the system-prompt resource handler to ensure it properly
 * exposes the system prompt through the MCP protocol.
 */

import { describe, it, expect, beforeEach, afterEach } from 'vitest';
import { E2ETestSetup } from './utils/e2e-test-setup.js';

describe('System Prompt Resource', () => {
  let testSetup: E2ETestSetup;

  beforeEach(async () => {
    testSetup = new E2ETestSetup();
    await testSetup.setup();
  });

  afterEach(async () => {
    await testSetup.cleanup();
  });

  it('should expose system prompt as MCP resource', async () => {
    // Start development to ensure conversation context exists
    await testSetup.callTool('start_development', { workflow: 'waterfall' });

    // Get the system prompt resource
    const resource = await testSetup.getResource('system-prompt://current');

    // Verify resource structure
    expect(resource).toBeDefined();
    expect(resource.uri).toBe('system-prompt://current');
    expect(resource.mimeType).toBe('text/plain');
    expect(resource.text).toBeDefined();
    expect(typeof resource.text).toBe('string');

    // Verify content contains expected system prompt elements
    expect(resource.text).toContain('You are an AI assistant that helps users develop software features');
    expect(resource.text).toContain('responsible-vibe-mcp');
    expect(resource.text).toContain('whats_next()');
    expect(resource.text).toContain('proceed_to_phase()');
    expect(resource.text).toContain('Core Workflow');

    // Verify it's a substantial prompt (not empty or truncated)
    expect(resource.text.length).toBeGreaterThan(1000);
  });

  it('should generate system prompt based on current workflow', async () => {
    // Start development with a specific workflow
    await testSetup.callTool('start_development', { workflow: 'epcc' });

    // Get the system prompt resource
    const resource = await testSetup.getResource('system-prompt://current');

    // Verify the prompt is generated (should contain standard elements regardless of workflow)
    expect(resource.text).toContain('You are an AI assistant');
    expect(resource.text).toContain('whats_next()');
    expect(resource.text).toContain('Development Workflow');
  });

  it('should handle resource request without active conversation', async () => {
    // Try to get system prompt without starting development first
    // This should still work as it falls back to default workflow
    const resource = await testSetup.getResource('system-prompt://current');

    expect(resource).toBeDefined();
    expect(resource.text).toContain('You are an AI assistant');
    expect(resource.mimeType).toBe('text/plain');
  });
});

