/**
 * System Prompt Resource Tests
 * 
 * Tests for the system-prompt resource handler to ensure it properly
 * exposes the system prompt through the MCP protocol.
 */

import { describe, it, expect, beforeEach, afterEach } from 'vitest';
import { E2ETestSetup } from './utils/e2e-test-setup.js';

describe('System Prompt Resource', () => {
  let testSetup: E2ETestSetup;

  beforeEach(async () => {
    testSetup = new E2ETestSetup();
    await testSetup.setup();
  });

  afterEach(async () => {
    await testSetup.cleanup();
  });

  it('should expose system prompt as MCP resource', async () => {
    // Get the system prompt resource (no need to start development first)
    const resource = await testSetup.readResource('system-prompt://');

    // Verify resource structure
    expect(resource).toBeDefined();
    expect(resource.contents).toBeDefined();
    expect(resource.contents).toHaveLength(1);

    const content = resource.contents[0];
    expect(content.uri).toBe('system-prompt://');
    expect(content.mimeType).toBe('text/plain');
    expect(content.text).toBeDefined();
    expect(typeof content.text).toBe('string');

    // Verify content contains expected system prompt elements
    expect(content.text).toContain('You are an AI assistant that helps users develop software features');
    expect(content.text).toContain('responsible-vibe-mcp');
    expect(content.text).toContain('whats_next()');
    expect(content.text).toContain('proceed_to_phase()');
    expect(content.text).toContain('Core Workflow');

    // Verify it's a substantial prompt (not empty or truncated)
    expect(content.text.length).toBeGreaterThan(1000);
  });

  it('should be workflow-independent', async () => {
    // Get system prompt without any active conversation
    const resource1 = await testSetup.readResource('system-prompt://');

    // Start development with a specific workflow
    await testSetup.callTool('start_development', { workflow: 'epcc' });

    // Get system prompt again
    const resource2 = await testSetup.readResource('system-prompt://');

    // Both should be identical since system prompt is workflow-independent
    expect(resource1.contents[0].text).toBe(resource2.contents[0].text);
  });

  it('should always return the same system prompt', async () => {
    // Get system prompt multiple times
    const resource1 = await testSetup.readResource('system-prompt://');
    const resource2 = await testSetup.readResource('system-prompt://');
    const resource3 = await testSetup.readResource('system-prompt://');

    // All should be identical
    expect(resource1.contents[0].text).toBe(resource2.contents[0].text);
    expect(resource2.contents[0].text).toBe(resource3.contents[0].text);

    // Verify the prompt contains standard elements
    expect(resource1.contents[0].text).toContain('You are an AI assistant');
    expect(resource1.contents[0].text).toContain('whats_next()');
    expect(resource1.contents[0].text).toContain('Development Workflow');
  });
});

