  it('should expose system prompt as MCP resource', async () => {
    const handler = new SystemPromptResourceHandler();

    // Call the handler directly
    const result = await handler.handle(new URL('system-prompt://'), {} as any);

    // Verify resource structure
    expect(result).toBeDefined();
    expect(result.uri).toBe('system-prompt://');
    expect(result.mimeType).toBe('text/plain');
    expect(result.text).toBeDefined();
    expect(typeof result.text).toBe('string');

    // Verify content contains expected system prompt elements
    expect(result.text).toContain('You are an AI assistant that helps users develop software features');
    expect(result.text).toContain('responsible-vibe-mcp');
    expect(result.text).toContain('whats_next()');
    expect(result.text).toContain('proceed_to_phase()');
    expect(result.text).toContain('Core Workflow');

    // Verify it's a substantial prompt (not empty or truncated)
    expect(result.text.length).toBeGreaterThan(1000);
  });

  it('should be workflow-independent and consistent', async () => {
    const handler = new SystemPromptResourceHandler();

    // Get system prompt multiple times
    const result1 = await handler.handle(new URL('system-prompt://'), {} as any);
    const result2 = await handler.handle(new URL('system-prompt://'), {} as any);
    const result3 = await handler.handle(new URL('system-prompt://'), {} as any);

    // All should be identical
    expect(result1.text).toBe(result2.text);
    expect(result2.text).toBe(result3.text);

    // Verify the prompt contains standard elements
    expect(result1.text).toContain('You are an AI assistant');
    expect(result1.text).toContain('whats_next()');
    expect(result1.text).toContain('Development Workflow');
    expect(result1.text).toContain('start_development()');
  });

  it('should use default waterfall workflow for system prompt', async () => {
    const handler = new SystemPromptResourceHandler();

    const result = await handler.handle(new URL('system-prompt://'), {} as any);

    // The system prompt should be generated using the default workflow
    // and should contain workflow-agnostic instructions
    expect(result.text).toContain('The responsible-vibe-mcp server will guide you through development phases');
    expect(result.text).toContain('available phases and their descriptions will be provided');
    expect(result.text).toContain('tool responses from start_development() and resume_workflow()');
  });
});

