/**
 * System Prompt MCP Discovery Test
 * 
 * Tests that the system-prompt resource is properly exposed through the MCP protocol
 * and can be discovered by MCP clients.
 */

import { describe, it, expect, beforeEach, afterEach, vi } from 'vitest';
import { TempProject } from './utils/temp-files.js';
import { DirectServerInterface, createSuiteIsolatedE2EScenario } from './utils/e2e-test-setup.js';

vi.unmock('fs');
vi.unmock('fs/promises');

describe('System Prompt MCP Discovery', () => {
  let tempProject: TempProject;
  let server: DirectServerInterface;

  beforeEach(async () => {
    const scenario = await createSuiteIsolatedE2EScenario('system-prompt-mcp-discovery');
    tempProject = scenario.tempProject;
    server = scenario.server;
  });

  afterEach(async () => {
    await tempProject.cleanup();
  });

  it('should expose system-prompt resource in resource listing', async () => {
    // List all available resources
    const resources = await server.listResources();

    expect(resources.resources).toBeDefined();
    expect(resources.resources).toHaveLength(3);

    // Find the system-prompt resource
    const systemPromptResource = resources.resources.find(r => r.uri === 'system-prompt://');

    expect(systemPromptResource).toBeDefined();
    expect(systemPromptResource?.uri).toBe('system-prompt://');
  });

  it('should be able to read system-prompt resource through MCP protocol', async () => {
    // This tests the actual MCP resource access path
    const resource = await server.readResource('system-prompt://');

    expect(resource).toBeDefined();
    expect(resource.contents).toBeDefined();
    expect(resource.contents).toHaveLength(1);

    const content = resource.contents[0];
    expect(content.uri).toBe('system-prompt://');
    expect(content.mimeType).toBe('text/plain');
    expect(content.text).toBeDefined();
    expect(typeof content.text).toBe('string');

    // Verify it contains the expected system prompt content
    expect(content.text).toContain('You are an AI assistant that helps users develop software features');
    expect(content.text).toContain('responsible-vibe-mcp');
    expect(content.text).toContain('whats_next()');
    expect(content.text.length).toBeGreaterThan(1000);
  });

  it('should provide proper resource metadata', async () => {
    const resources = await server.listResources();
    const systemPromptResource = resources.resources.find(r => r.uri === 'system-prompt://');

    expect(systemPromptResource).toBeDefined();
    expect(systemPromptResource?.uri).toBe('system-prompt://');

    // The resource should be discoverable without additional metadata requirements
    // This confirms it's properly registered with the MCP server
  });
});

