 * 
 * Tests that the system-prompt resource is properly exposed through the MCP protocol
 * and can be discovered by MCP clients.
 */

import { describe, it, expect } from 'vitest';
import { SystemPromptResourceHandler } from '../src/server/resource-handlers/system-prompt.js';

describe('System Prompt MCP Discovery', () => {

  it('should properly register and expose system-prompt resource', async () => {
    // Test that the resource handler works correctly
    const handler = new SystemPromptResourceHandler();

    const result = await handler.handle(new URL('system-prompt://'), {} as any);

    expect(result.success).toBe(true);
    expect(result.data).toBeDefined();
    expect(result.data.uri).toBe('system-prompt://');
    expect(result.data.mimeType).toBe('text/plain');
    expect(result.data.text).toBeDefined();
    expect(typeof result.data.text).toBe('string');

    // Verify it contains the expected system prompt content
    expect(result.data.text).toContain('You are an AI assistant that helps users develop software features');
    expect(result.data.text).toContain('responsible-vibe-mcp');
    expect(result.data.text).toContain('whats_next()');
    expect(result.data.text.length).toBeGreaterThan(1000);
  });

  it('should be workflow-independent', async () => {
    const handler = new SystemPromptResourceHandler();

    // Multiple calls should return identical content
    const result1 = await handler.handle(new URL('system-prompt://'), {} as any);
    const result2 = await handler.handle(new URL('system-prompt://'), {} as any);

    expect(result1.success).toBe(true);
    expect(result2.success).toBe(true);
    expect(result1.data.text).toBe(result2.data.text);
  });
});

