#!/usr/bin/env node

/**
 * Simple test script to verify the system-prompt resource is properly exposed
 * through the MCP server.
 */

import { spawn } from 'child_process';
import { fileURLToPath } from 'url';
import { dirname, join } from 'path';

const __filename = fileURLToPath(import.meta.url);
const __dirname = dirname(__filename);

async function testSystemPromptResource() {
  console.log('🧪 Testing system-prompt resource exposure...\n');

  // Start the MCP server
  const serverProcess = spawn('node', [join(__dirname, 'dist/index.js')], {
    stdio: ['pipe', 'pipe', 'pipe']
  });

  let serverOutput = '';
  let serverError = '';

  serverProcess.stdout.on('data', (data) => {
    serverOutput += data.toString();
  });

  serverProcess.stderr.on('data', (data) => {
    serverError += data.toString();
  });

  // Send MCP initialization message
  const initMessage = {
    jsonrpc: "2.0",
    id: 1,
    method: "initialize",
    params: {
      protocolVersion: "2024-11-05",
      capabilities: {},
      clientInfo: {
        name: "test-client",
        version: "1.0.0"
      }
    }
  };

  console.log('📤 Sending initialization message...');
  serverProcess.stdin.write(JSON.stringify(initMessage) + '\n');

  // Wait for initialization response
  await new Promise(resolve => setTimeout(resolve, 1000));

  // Send resources/list request
  const listResourcesMessage = {
    jsonrpc: "2.0",
    id: 2,
    method: "resources/list"
  };

  console.log('📤 Requesting resource list...');
  serverProcess.stdin.write(JSON.stringify(listResourcesMessage) + '\n');

  // Wait for response
  await new Promise(resolve => setTimeout(resolve, 1000));

  // Send system-prompt resource read request
  const readResourceMessage = {
    jsonrpc: "2.0",
    id: 3,
    method: "resources/read",
    params: {
      uri: "system-prompt://"
    }
  };

  console.log('📤 Reading system-prompt resource...');
  serverProcess.stdin.write(JSON.stringify(readResourceMessage) + '\n');

  // Wait for response
  await new Promise(resolve => setTimeout(resolve, 1000));

  // Close the server
  serverProcess.kill();

  console.log('\n📋 Server Output:');
  console.log(serverOutput);

  if (serverError) {
    console.log('\n❌ Server Errors:');
    console.log(serverError);
  }

  // Check if system-prompt resource was found
  if (serverOutput.includes('system-prompt://')) {
    console.log('\n✅ SUCCESS: system-prompt resource is properly exposed!');
  } else {
    console.log('\n❌ FAILURE: system-prompt resource not found in output');
  }
}

testSystemPromptResource().catch(console.error);

